/**
 * @brief This replaces the bytes in a byte array by the given ones
 *
 * @param file_bytes the bytes array to modify
 * @param initial_offset the file offset from where to start replacing the bytes
 * @param new_bytes an array containing the bytes to replace starting on the given offset (can be as many as wanted, as
 * long as they don't go over the length of 'file_bytes' - this is NOT checked here)
 * @param num_new_bytes the length of 'new_bytes'
 */
void patchBytes(uint8_t *file_bytes, int initial_offset, uint8_t const *new_bytes, int num_new_bytes) {
	int i = 0;
	int offset = 0;
	for (i = 0, offset = initial_offset; i < num_new_bytes; ++offset, ++i) {
		file_bytes[offset] = new_bytes[i];
	}
}

/**
 * @brief Prints the initial screen with credits.
 */
void showInitialScreen(void) {
	printf("-------------------"NL);
	printf("F1DP v1.1 Installer"NL);
	printf("-------------------"NL);
	printf(NL);
	printf("This program will install the F1DP Loader into the game EXE. After completing, this program will be "
		   "no longer necessary and you may delete it if you want."NL);
	printf(NL);
	printf("-----Credits-----"NL);
	printf("[I'm not sure what the order should be in this case, as it's all very important, so partial "
		   "alphabetical order.]"NL);
	printf(NL);
	printf("- DADi590 (me), for making F1DP and adapting all patches to the DOS EXE"NL);
	printf("- Crafty for his sFall1 modifications"NL);
	printf("- Timeslip for the original sFall1 mod"NL);
	printf("- Sduibek for his patches"NL);
	printf("- TeamX for their patches"NL);
	printf("- xttl on the Doomworld forums for his posted knowledge, which helped making my life MUCH easier "
		   "while trying to figure out how to get the patcher to work on DOS (or even make this project possible, "
		   "in case I'd find it too hard to do with my available time without this knowledge)"NL);
	printf(NL);
	if (COMPILE_DOS) {
		// getchar() is not working well in DOSBox. I press ENTER, a new line appears, and it's like that until I reach
		// some number of characters (I can just keep pressing F until the program decides to go past getchar() - ???).
		printf("Credits completed, now 5 seconds to continue to the Installer..."NL);
		sleep(5);
	} else {
		printf("Credits completed, now press ENTER to continue to the Installer..."NL);
		fflush(stdout);
		getchar();
	}

	printf("------------------------------------------------------------------------"NL);
}

/**
 * @brief Wrapper of exit() but with added comments depending on given the exit code.
 *
 * @param exit_code one of the ERR_-started constants
 */
void exitNew(int exit_code) {
	printf(NL);
	if (0 != exit_code) {
		printf("ATTENTION: the Installer did not complete successfully. Please check the console for more information."NL);
		printf("Exiting with error code: %d"NL, exit_code);
	} else {
		printf("The Installer completed all operations successfully!"NL);
	}
	if (COMPILE_DOS) {
		printf("Exiting in 5 seconds..."NL);
		sleep(5);
	} else {
		printf("Press ENTER to exit..."NL);
		fflush(stdout);
		getchar();
	}

	exit(exit_code);
}

/**
 * @brief Gets the index with which to get the folder path from a file path.
 *
 * @param file_path the path to the file
 *
 * @return the index of the last backslash on the given file path, or -1 if not found
 */
int getFolderPathFromFilePath(char const *file_path) {
	int i = 0;
	int last_backslash_index = -1;
	for (i = 0; '\0' != file_path[i]; ++i) {
		if ('\\' == file_path[i]) {
			last_backslash_index = i;
		}
	}

	return last_backslash_index;
}
